import Home from "./Home"
import JoinNow from "./JoinNow"
import MyLife from "./MyLife"
import OFMIntroduction from "./OFMIntroduction"
import Opening from "./Opening"
import { useState, useEffect } from "react";


const MainProvider = () => {

    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        // Sayfa tamamen yüklendiğinde
        window.onload = () => {
            setIsLoaded(true);
        };

        // Cleanup için window.onload'ı sıfırlamak
        return () => {
            window.onload = null;
        };
    }, []);

    return(
        <>  

            {!isLoaded && <Opening />}  {/* Opening sadece yükleme tamamlanana kadar görünsün */}
            {isLoaded && (
                <>
                    <Home onImageLoad={handleImageLoad} />
                    <OFMIntroduction onImageLoad={handleImageLoad} />
                    <JoinNow onImageLoad={handleImageLoad} />
                    <MyLife onImageLoad={handleImageLoad} />
                </>
            )}
        </>
    )
}

export default MainProvider
