import React, {useState, useEffect} from "react"
import Home from "./Home"
import JoinNow from "./JoinNow"
import MyLife from "./MyLife"
import OFMIntroduction from "./OFMIntroduction"
import Opening from "./Opening"



const MainProvider = () => {

    const [isLoading, setIsLoading] = useState(true); // Yükleme durumu

    useEffect(() => {
        const componentsReady = [
            Opening.preload(), // Opening componentindeki resimler yüklensin
            Home.preload(),
            OFMIntroduction.preload(),
            JoinNow.preload(),
            MyLife.preload(),
        ];

        // Tüm preload'lar tamamlanınca isLoading'i false yap
        Promise.all(componentsReady)
            .then(() => setIsLoading(false))
            .catch((err) => console.error("Yükleme sırasında hata:", err));
    }, []);

    return(
        <>  

        </>
    )
}

export default MainProvider
