import React, { useState, useEffect } from "react";
import Loading from "./Loading";
import { preloadOpening } from "./Opening";
import { preloadHome } from "./Home";
import { preloadOFMIntroduction } from "./OFMIntroduction";
import { preloadJoinNow } from "./JoinNow";
import { preloadMyLife } from "./MyLife";


const MainProvider = () => {
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // Tüm preload fonksiyonlarını çağır
        const componentsReady = [
            preloadOpening(),
            preloadHome(),
            preloadOFMIntroduction(),
            preloadJoinNow(),
            preloadMyLife(),
        ];

        Promise.all(componentsReady)
            .then(() => setIsLoading(false))
            .catch((err) => console.error("Yükleme sırasında hata:", err));
    }, []);

    return (
        <div>
            {isLoading ? (
                <Loading />
            ) : (
                <>
                    <Opening />
                    <Home />
                    <OFMIntroduction />
                    <JoinNow />
                    <MyLife />
                </>
            )}
        </div>
    );
};

export default MainProvider;
